import React from 'react';

const ItemBasicDetails = props => {
  const {name, packageId, description = '', author, version, authorFromPackage, defaultAuthor} = props;

  let authorForDoc;

  if (author) {
    authorForDoc = author;
  } else if (authorFromPackage) {
    authorForDoc = authorFromPackage;
  } else {
    authorForDoc = defaultAuthor;
  }

  return (
    <>
      <h1 className="schema-title">{name}</h1>
      <div>
        <span className="schema-details-key">Package: </span>
        <span className="schema-details-value">{packageId}</span>
      </div>
      <div>
        <span className="schema-details-key">Version: </span>
        <span className="schema-details-value">{version}</span>
      </div>
      <div>
        <span className="schema-details-key">Author: </span>
        <span className="schema-details-value">{authorForDoc}</span>
      </div>

      <div className="schema-description">
        <h2>Description</h2>
        <div>{description}</div>
      </div>
    </>
  );
};

export default ItemBasicDetails;
