import React, {useState} from 'react';
import {t} from '@oracle-cx-commerce/utils/generic';
import DynamicDropdownButton from './dynamic-dropdown-button';

const Menu = props => {
  const {currentPageItemName, basePageURL, state, currentURL, textLabels} = props;
  const {gettingStarted} = textLabels;
  const [allExpanded, setAllExpanded] = useState(false);

  return (
    <div>
      <div className="dropdown-content level-0">
        <button
          type="button"
          className="right-align"
          onClick={() => {
            setAllExpanded(!allExpanded);
          }}
        >
          {allExpanded ? 'Collapse All -' : 'Expand All +'}
        </button>
      </div>
      <div className={`dropdown-content level-0 ${!currentPageItemName ? 'selected_dropdown-content' : ''}`}>
        <a href={`${basePageURL}#`}> {t(gettingStarted)}</a>
      </div>
      <DynamicDropdownButton
        state={state}
        currentURL={currentURL}
        basePageURL={basePageURL}
        textLabels={textLabels}
        allExpanded={allExpanded}
      />
    </div>
  );
};

export default Menu;
