import React from 'react';

const PropsTable = props => {
  const {Props} = props;

  return Object.keys(Props).length !== 0 ? (
    <>
      <tr>
        <th>Name</th>
        <th>Description</th>
        <th>Type</th>
        <th>Is Required</th>
      </tr>
      {Object.keys(Props).map(prop => (
        <tr key={Props[prop].name}>
          <td> {Props[prop].name}</td>
          <td> {Props[prop].description}</td>
          <td> {Props[prop].type ? Props[prop].type.name : null}</td>
          <td>{Props[prop].required ? 'True' : 'False'}</td>
        </tr>
      ))}
    </>
  ) : (
    <tr>
      <td>N.A.</td>
    </tr>
  );
};

export default PropsTable;
