import React from 'react';

const ResourceTable = props => {
  const {enResources} = props;

  return (
    <>
      {Object.keys(enResources).map(resource => (
        <tr key={resource}>
          <td>{resource} : </td>
          <td>{enResources[resource]}</td>
        </tr>
      ))}
    </>
  );
};

export default ResourceTable;
