import React from 'react';

const StyleTable = props => {
  const {styles = null} = props;

  return styles !== null && Object.keys(styles).length !== 0 ? (
    <>
      <tr>
        <td></td>
        <td>
          <pre>{styles}</pre>
        </td>
      </tr>
    </>
  ) : null;
};

export default StyleTable;
