export const togglePluginDropdown = plugin => {
  document.querySelector(`#${plugin}`).classList.toggle('show');
  document.querySelector(`#${plugin}`).classList.toggle('visible');
};

export const fillSubCategoryMap = (categoryList = {}) => {
  const SubCategoryMap = new Map();
  for (const subCat in categoryList) {
    const listLength = categoryList[subCat].length;
    for (let catListIdx = 0; catListIdx < listLength; catListIdx++) {
      SubCategoryMap.set(categoryList[subCat][catListIdx].name, subCat);
    }
  }

  return SubCategoryMap;
};

export const trimCompletedActions = action => {
  if (action.slice(action.length - 8) === 'Complete') {
    return action.slice(0, action.length - 8);
  }

  return action;
};
