import {
  getCurrentOrder,
  isCurrentUserB2B,
  getCartEndpointStatus,
  getShippingCountries,
  getSite
} from '@oracle-cx-commerce/commerce-utils/selector';

export const getComponentData = state => {
  const currentOrder = getCurrentOrder(state);
  const shippingCountries = getShippingCountries(state);
  const defaultBillingCountryCode = getSite(state).defaultBillingCountryId;
  const isB2BUser = isCurrentUserB2B(state);
  const isGetCartInProgress = getCartEndpointStatus(state);

  return {
    currentOrder,
    isB2BUser,
    isGetCartInProgress,
    countryList: shippingCountries,
    defaultCountry: defaultBillingCountryCode
  };
};
