import * as resourceBundle from '@oracle-cx-commerce/resources';

import {buildConfigResources} from '@oracle-cx-commerce/resources/utils';
import {mergeDefaultConfig} from '@oracle-cx-commerce/react-widgets/config';

import {
  cartItemConfigProperties,
  cartItemDetailsConfigResourceKeys
} from '@oracle-cx-commerce/react-widgets/cart/cart-item-details/config';
import {
  productDeliveryOptionsConfigProperties,
  productDeliveryOptionsConfigResourceKeys
} from '@oracle-cx-commerce/react-widgets/product/product-delivery-options/config';

const configResourceKeys = [...productDeliveryOptionsConfigResourceKeys, ...cartItemDetailsConfigResourceKeys];

const extendedCartItemConfig = mergeDefaultConfig({
  properties: [...productDeliveryOptionsConfigProperties, ...cartItemConfigProperties],
  locales: buildConfigResources(resourceBundle, configResourceKeys)
});

export default extendedCartItemConfig;
