import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/category/collection-navigation-desktop/components/drop-down-media/styles.css';

/**
 * Media component to display promotional content or category images in the sub menu
 */
const MenuMediaMobile = ({categoryMedia = ''}) => {
  function getCategoryMedia() {
    return {__html: categoryMedia};
  }

  return (
    <Styled id="CollectionNavigationMobile__MenuMediaMobile" css={css}>
      <div className="CollectionNavigationMobile__MenuMediaMobile" dangerouslySetInnerHTML={getCategoryMedia()}></div>
    </Styled>
  );
};

export default React.memo(MenuMediaMobile);
