import React, {useContext} from 'react';

import BackNavButton from '@oracle-cx-commerce/react-widgets/category/collection-navigation/components/back-nav-button';
import BackToRootButton from '@oracle-cx-commerce/react-widgets/category/collection-navigation/components/back-to-root-button';
import CloseMenuButton from '@oracle-cx-commerce/react-widgets/category/collection-navigation/components/close-menu-button';
import {CollectionNavCardContext} from '@oracle-cx-commerce/react-widgets/category/collection-navigation/context';
import ConfigurableRegion from '@oracle-cx-commerce/react-widgets/category/collection-navigation/components/configurable-region';
import Heading from '@oracle-cx-commerce/react-widgets/category/collection-navigation/components/heading';

/**
 * Represents the header for a view of collection and it's child collections.
 */
const ChildCollectionHeader = () => {
  const {
    category: {displayName}
  } = useContext(CollectionNavCardContext);

  return (
    <React.Fragment>
      <CloseMenuButton />
      <BackToRootButton />
      <ConfigurableRegion regionIndex="0" />
      <Heading text={displayName} />
      <BackNavButton />
    </React.Fragment>
  );
};

ChildCollectionHeader.propTypes = {};

ChildCollectionHeader.defaultProps = {};

export default React.memo(ChildCollectionHeader);
