import React, {useContext} from 'react';

import {CollectionNavContext} from '@oracle-cx-commerce/react-widgets/category/collection-navigation/context';
import PropTypes from 'prop-types';
import Region from '@oracle-cx-commerce/react-components/region';

/**
 * Provides a region that hosts design studio widgets.
 */
const ConfigurableRegion = ({regionIndex}) => {
  const {
    config: {regions = []}
  } = useContext(CollectionNavContext);

  return (
    <React.Fragment>
      {regions.length > 0 && <Region key={regions[regionIndex]} regionId={regions[regionIndex]} />}
    </React.Fragment>
  );
};

ConfigurableRegion.propTypes = {
  /** The key of the region to be displayed in the configurable region */
  regionIndex: PropTypes.string.isRequired
};

ConfigurableRegion.defaultProps = {};

export default ConfigurableRegion;
