import React from 'react';
import PropTypes from 'prop-types';

/**
 * Provides a heading with the specified text
 */
const Heading = ({text}) => {
  return (
    <div className="CollectionNavigation__MenuTitle">
      <h2 tabIndex="-1">{text}</h2>
    </div>
  );
};

Heading.propTypes = {
  /** Route of the category */
  text: PropTypes.string.isRequired
};

Heading.defaultProps = {};

export default Heading;
