import {
  CollectionNavCardContext,
  CollectionNavContext
} from '@oracle-cx-commerce/react-widgets/category/collection-navigation/context';
import React, {useContext} from 'react';

import Link from '@oracle-cx-commerce/react-components/link';
import PropTypes from 'prop-types';

const MemoizedLink = React.memo(Link);

/**
 * Provides a link to view the current collection
 */
const ViewAllLink = ({route}) => {
  const {
    config: {labelViewAll, viewAllOption},
    onNavigateToCategory
  } = useContext(CollectionNavContext);

  const {index, isVisible} = useContext(CollectionNavCardContext);

  return (
    <React.Fragment>
      {viewAllOption && (
        <li className="CollectionNavigation__NavItem">
          <MemoizedLink href={route} onNavigate={onNavigateToCategory} tabIndex={isVisible(index) ? 0 : -1}>
            {labelViewAll}
          </MemoizedLink>
        </li>
      )}
    </React.Fragment>
  );
};

ViewAllLink.propTypes = {
  /** Route of the category */
  route: PropTypes.string.isRequired
};

ViewAllLink.defaultProps = {};

export default ViewAllLink;
