import {getPaymentConfigurations} from '@oracle-cx-commerce/commerce-utils/selector';
import {PAYMENT_TYPE_GIFTCARD} from '@oracle-cx-commerce/commerce-utils/constants';
import {getPaymentEnabledForScheduledOrder} from '@oracle-cx-commerce/react-components/utils/payment';

export const getCheckoutGiftCardData = state => {
  const {paymentMethods = []} = getPaymentConfigurations(state);

  return {
    isDisplayGiftCard: paymentMethods.includes(PAYMENT_TYPE_GIFTCARD),
    PaymentInfoForScheduledOrder: getPaymentEnabledForScheduledOrder(state, PAYMENT_TYPE_GIFTCARD)
  };
};
