import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/checkout/checkout-pending-payment-header/styles.css';
import Alert from '@oracle-cx-commerce/react-components/alert';

/**
 * Widget for Complete Payment button.
 */
const CheckoutPendingPaymentHeader = props => {
  const {headingCheckout, textAdditionalPaymentInformationNeeded, textDoNotNavigateFromPendingPaymentPage} = props;

  return (
    <Styled id="CheckoutPendingPaymentHeader" css={css}>
      <div className="CheckoutPendingPaymentHeader">
        <h1 className="CheckoutPendingPaymentHeader__heading">{headingCheckout}</h1>
        <Alert
          id="CheckoutPendingPaymentHeader_Id"
          type="warning"
          message={
            <>
              <div>{textAdditionalPaymentInformationNeeded}</div>
              <div>{textDoNotNavigateFromPendingPaymentPage}</div>
            </>
          }
        ></Alert>
      </div>
    </Styled>
  );
};

export default CheckoutPendingPaymentHeader;
