import {getSessionContext, getProfile} from '@oracle-cx-commerce/commerce-utils/selector';

export const getComponentData = state => {
  const oboRedirectUrl = getSessionContext(state).redirectUrl ? getSessionContext(state).redirectUrl : '';
  const currentProfile = getProfile(state);
  const profileName =
    currentProfile && currentProfile.firstName ? `${currentProfile.firstName} ${currentProfile.lastName}` : '';

  return {
    oboRedirectUrl,
    profileName
  };
};
