import React from 'react';
import css from '@oracle-cx-commerce/react-widgets/common/company-logo/styles.css';
import {getComponentData} from '@oracle-cx-commerce/react-widgets/common/company-logo/selectors';
import {connect} from '@oracle-cx-commerce/react-components/provider';
import Styled from '@oracle-cx-commerce/react-components/styled';
import Img from '@oracle-cx-commerce/react-components/img';
import PropTypes from 'prop-types';

/**
 * A component for showing account Logo that a profile is part of.
 * @param props
 */
const CompanyLogo = props => {
  const {isUserLoggedIn, currentOrganizationId, organizationLogoURL, organizationName} = props;

  return (
    <Styled id="CompanyLogo" css={css}>
      {isUserLoggedIn && currentOrganizationId && (
        <div className="CompanyLogo">
          {<Img src={organizationLogoURL} alt={organizationName} title={organizationName} size="xsmall"></Img>}
        </div>
      )}
    </Styled>
  );
};

CompanyLogo.propTypes = {
  /**
   * This value is used to check if the user is logged in
   */
  isUserLoggedIn: PropTypes.string,
  /**
   * This value is used to indicate the current organization id of a B2B user
   */
  currentOrganizationId: PropTypes.string,
  /**
   * This value is used to indicate the organization name of a B2B account
   */
  organizationName: PropTypes.string.isRequired,
  /**
   * This value is used to indicate the organization logo of a B2B account
   */
  organizationLogoURL: PropTypes.string
};

CompanyLogo.defaultProps = {
  isUserLoggedIn: false,
  currentOrganizationId: undefined,
  organizationLogoURL: ''
};
export default connect(getComponentData)(CompanyLogo);
