import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import {connect} from '@oracle-cx-commerce/react-components/provider';
import css from '@oracle-cx-commerce/react-widgets/common/error/styles.css';
import {getRequestContext} from '@oracle-cx-commerce/commerce-utils/selector';
import {t} from '@oracle-cx-commerce/utils/generic';

const Error = props => {
  const {page, message404, title404} = props;

  return (
    <Styled id="Error" css={css}>
      <div className="Error">
        <h1>{t(title404)}</h1>
        <p>{t(message404, {ADDRESS: page})}</p>
      </div>
    </Styled>
  );
};

export default connect(getRequestContext)(Error);
