import React from 'react';
import Region from '@oracle-cx-commerce/react-components/region';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/common/footer-container/styles.css';

/**
 * Following Footer Container component will hold the widgets related to Footer.
 *
 * @param props
 */

const FooterContainer = props => {
  const {regions = []} = props;

  return (
    <Styled id="FooterContainer" css={css}>
      {/* render each child region */}
      <section className="FooterContainer__Section">
        {regions.map((regionId, index) => (
          /*
            Using region ids as keys causes unnecessary DOM reconciliation.
              
            https://reactjs.org/docs/reconciliation.html#keys
          */
          <Region key={index} regionId={regionId} /> // eslint-disable-line react/no-array-index-key
        ))}
      </section>
    </Styled>
  );
};

export default FooterContainer;
