import React from 'react';
import Tab from './tab';

const PlaceholderStack = props => {
  const {steps = [], width = 12, displayName} = props;

  return (
    <section className={`Region col-${width}`}>
      <div>Stack - {displayName}</div>
      {steps.map(id => (
        <Tab key={id} regionId={id} />
      ))}
    </section>
  );
};

export default PlaceholderStack;
