import CheckCircleIcon from '@oracle-cx-commerce/react-components/icons/check-circle';
import Link from '@oracle-cx-commerce/react-components/link';
import Modal from '@oracle-cx-commerce/react-components/modal';
import {PAGE_CART_LINK} from '@oracle-cx-commerce/commerce-utils/constants';
import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/common/quick-order/components/added-to-cart-modal/style.css';
import {t} from '@oracle-cx-commerce/utils/generic';
/**
 * Component to display alert modal
 * for merging the items to cart
 */
const AddedToCartModal = props => {
  const {showModal = () => {}, closeModal = () => {}, noOfItems, resources} = props;

  return (
    <Styled id="AddedToCart" css={css}>
      <Modal
        className="AddedToCartModal"
        show={showModal}
        onClose={closeModal}
        closeIconTitle={resources.closeLinkAltText}
        closeArialLabel={resources.closeLinkAltText}
      >
        {showModal && (
          <section className="AddedToCartModal__Main">
            <div>
              <CheckCircleIcon className="AddedToCartModal__Icon" />
              <span className="AddedToCartModal__Header">{resources.alertAddedToCart}</span>
            </div>

            <div className="AddedToCartModal__Content">{t(resources.textQuickOrderAddToCartContent, {noOfItems})}</div>

            <div className="AddedToCartModal__Link">
              <Link href={PAGE_CART_LINK}>{resources.textViewCart}</Link>
            </div>
          </section>
        )}
      </Modal>
    </Styled>
  );
};

export default React.memo(AddedToCartModal);
