import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/common/web-content/styles.css';

/**
 * Following Widget component renders the HTML content that is configured.
 *
 * @param props
 */

const WebContent = props => {
  return (
    <Styled id="WebContent" css={css}>
      <div className="WebContent__Html" dangerouslySetInnerHTML={{__html: props.webContent}}></div>
    </Styled>
  );
};

export default WebContent;
