import {buildConfigResources} from '@oracle-cx-commerce/resources/utils';
import * as resourceBundle from '@oracle-cx-commerce/resources';
import {mergeDefaultConfig} from '@oracle-cx-commerce/react-widgets/config';

const contentTextConfigResourceKeys = ['configContentTextPickerLabel', 'configContentTextHelpText'];
const contentTextConfigProperties = [
  {
    id: 'settings',
    type: 'contentPickerType',
    helpTextResourceId: 'configContentTextHelpText',
    labelResourceId: 'configContentTextPickerLabel',
    required: true
  }
];

const config = mergeDefaultConfig({
  properties: [...contentTextConfigProperties],
  locales: buildConfigResources(resourceBundle, contentTextConfigResourceKeys)
});

export default config;
