import {getCategory, getPage} from '@oracle-cx-commerce/commerce-utils/selector';

export const getComponentData = state => {
  const {contextId: categoryId} = getPage(state);
  const {categoryImages = [], longDescription = '', description = ''} = getCategory(state, {categoryId});

  return {
    categoryImages,
    longDescription,
    description
  };
};
