import {ContainerContext} from '@oracle-cx-commerce/react-ui/contexts';
import React, {useContext} from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/product-listing/category-description/styles.css';

/**
 * Widget that displays short description of a particular category
 */
const CategoryDescription = () => {
  const {description: shortDescription = ''} = useContext(ContainerContext);

  return (
    <Styled id="CategoryDescription" css={css}>
      <div className="CategoryDescription">
        <div>{shortDescription}</div>
      </div>
    </Styled>
  );
};

export default CategoryDescription;
