/* eslint-disable react/no-danger */
import React, {useContext} from 'react';
import {ContainerContext} from '@oracle-cx-commerce/react-ui/contexts';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/product-listing/category-long-description/styles.css';

/**
 * Widget that displays long description of a particular category
 */
const CategoryLongDescription = () => {
  // context
  const {longDescription = ''} = useContext(ContainerContext);

  if (!longDescription) {
    return null;
  }

  return (
    <Styled id="CategoryLongDescription" css={css}>
      <div className="CategoryLongDescription">
        <div className="CategoryLongDescription__Html" dangerouslySetInnerHTML={{__html: longDescription}}></div>
      </div>
    </Styled>
  );
};

export default CategoryLongDescription;
