import {useContext} from 'react';
import {getCurrentOrder, getProduct} from '@oracle-cx-commerce/commerce-utils/selector';
import {useSelector} from '@oracle-cx-commerce/react-components/provider';
import {ProductContext} from '@oracle-cx-commerce/react-ui/contexts';

export const useComponentData = () => {
  const {id: productId} = useContext(ProductContext);

  // selectors
  const {shippingGroups = []} = useSelector(getCurrentOrder);

  const {addOnProducts = []} = useSelector(getProduct, {productId});

  return {shippingGroups, addOnProducts};
};
