import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/product/product-configure/components/actions/styles.css';
import PropTypes from 'prop-types';
import {noop} from '@oracle-cx-commerce/utils/generic';

/**
 * This component represents a button for configuration actions.
 *
 * @param {String} buttonText
 *   The text to be displayed on the button.
 * @param {String} handler
 *   The handler that will be invoked when the button is clicked.
 * @param {Boolean} isDisabled
 *   Flag that determines whether the button is disabled or not.
 */
const ActionButton = ({buttonText = '', handler = noop, isDisabled = false, isPrimary = false}) => {
  return (
    <Styled id="ConfiguratorActionButton" css={css}>
      <button
        type="button"
        className={`ConfiguratorActionButton ${isPrimary ? '' : 'secondary'}`}
        disabled={isDisabled}
        onClick={handler}
      >
        {buttonText}
      </button>
    </Styled>
  );
};

ActionButton.propTypes = {
  /** The button label */
  buttonText: PropTypes.string,
  /** The configData object from the configurations repository store. */
  handler: PropTypes.func,
  /** productFamily attribute of the sku */
  isDisabled: PropTypes.bool,
  /** productLine attribute of the sku */
  isPrimary: PropTypes.bool
};

ActionButton.defaultProps = {
  buttonText: '',
  handler: noop,
  isDisabled: false,
  isPrimary: false
};

export default ActionButton;
