import React, {useContext} from 'react';
import Img from '@oracle-cx-commerce/react-components/img';
import Styled from '@oracle-cx-commerce/react-components/styled';
import {ConfiguratorContext} from '@oracle-cx-commerce/react-widgets/product/product-configure/contexts';
import css from '@oracle-cx-commerce/react-widgets/product/product-configure/components/product-details/styles.css';

/**
 * A widget to display the product image and title in the configurator
 * modal dialog.
 */
const ProductDetails = () => {
  const {productDisplayName, productThumbImageURL} = useContext(ConfiguratorContext);

  return (
    <Styled id="ConfiguratorProductDetails" css={css}>
      {productDisplayName && productThumbImageURL && (
        <div className="ConfiguratorProductDetails__Container">
          <Img
            className="ConfiguratorProductDetails__ProductImage"
            src={productThumbImageURL}
            title={productDisplayName}
            alt={productDisplayName}
            size="xsmall"
          />
          <div className="ConfiguratorProductDetails__ProductDisplayName">{productDisplayName}</div>
        </div>
      )}
    </Styled>
  );
};

ProductDetails.propTypes = {};

export default ProductDetails;
