import React from 'react';
import Radio from '@oracle-cx-commerce/react-components/radio';
import Styled from '@oracle-cx-commerce/react-components/styled';
import {noop} from '@oracle-cx-commerce/utils/generic';
import css from '@oracle-cx-commerce/react-widgets/product/product-delivery-options/components/delivery-option/styles.css';

/**
 * Displays a product delivery option.
 */
const DeliveryOption = props => {
  const {id, name, onClick, label, 'data-testid': dataTestId = '', storeLocatorLink = '', isOptionSelected} = props;

  return (
    <Styled id="DeliveryOption" css={css}>
      <div className="DeliveryOption">
        <div className="DeliveryOption__RadioButton">
          <Radio
            id={id}
            name={name}
            data-testid={dataTestId}
            required={true}
            onClick={onClick}
            onChange={noop}
            readOnly={true}
            checked={isOptionSelected}
            labelText={
              <span className={`DeliveryOption__Label ${isOptionSelected ? 'DeliveryOption__Selected' : ''}`}>
                {label}
              </span>
            }
          />
        </div>
        {storeLocatorLink}
      </div>
    </Styled>
  );
};

export default React.memo(DeliveryOption);
