import React, {useContext} from 'react';
import {ProductContext} from '@oracle-cx-commerce/react-ui/contexts';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/product/product-short-description/styles.css';

/**
 * Displays a product's description using the description property from the product.
 */
export default function ProductShortDescription() {
  // selector
  const {description = ''} = useContext(ProductContext);

  return (
    <Styled id="ProductShortDescription" css={css}>
      <span className="ProductShortDescription">{description}</span>
    </Styled>
  );
}
