import React, {Suspense, useState} from 'react';

import Popover from '@oracle-cx-commerce/react-components/popover';
import ShareIcon from '@oracle-cx-commerce/react-components/icons/social-sharing';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/product/product-social-sharing/styles.css';

const ProductSocialSharingPortal = React.lazy(() =>
  import('@oracle-cx-commerce/react-widgets/product/product-social-sharing/components/product-social-sharing-portal')
);

/**
 * Displays Product Social sharing icons to share a product on social media
 */
const ProductSocialSharing = props => {
  const {
    actionShare,
    headingShareThisVia,
    closeLinkAltText,
    displayFacebook = true,
    displayPinterest = true,
    displayTwitter = true,
    displayEmail = true
  } = props;

  const [showShareOptions, setShowShareOptions] = useState(false);
  const displaySocialShareLink = displayFacebook || displayPinterest || displayTwitter || displayEmail;

  return (
    <Styled id="ProductSocialSharing" css={css}>
      {displaySocialShareLink && (
        <div className="ProductSocialSharing">
          <button
            className="ProductSocialSharing__PrimaryIcon"
            onClick={() => setShowShareOptions(!showShareOptions)}
            type="button"
            aria-label={actionShare}
          >
            <ShareIcon />
            <span className="ProductSocialSharing__ShareText">{actionShare}</span>
          </button>

          {showShareOptions && typeof window !== 'undefined' && (
            <Suspense fallback={null}>
              <Popover
                show={showShareOptions}
                title={headingShareThisVia}
                onClose={() => setShowShareOptions(false)}
                closeIconTitle={closeLinkAltText}
              >
                <ProductSocialSharingPortal {...props} />
              </Popover>
            </Suspense>
          )}
        </div>
      )}
    </Styled>
  );
};

export default ProductSocialSharing;
