import * as resourceBundle from '@oracle-cx-commerce/resources';
import config from '@oracle-cx-commerce/react-widgets/product/product-social-sharing/config';
import {mergeDefaultConfig} from '@oracle-cx-commerce/react-widgets/config';
import {buildResources} from '@oracle-cx-commerce/resources/utils';

const widgetResourceKeys = [
  'textEmailBody',
  'textEmailSubject',
  'actionShare',
  'headingShareThisVia',
  'actionShareByEmail',
  'actionShareOnFacebook',
  'actionShareOnPinterest',
  'actionShareOnTwitter',
  'closeLinkAltText',
  'textTwitterShare',
  'textPinterestShare'
];

export const ProductSocialSharing = {
  packageId: '@oracle-cx-commerce/react-widgets',
  initializers: ['productionFacebook'],
  resources: buildResources(resourceBundle, widgetResourceKeys),
  config: mergeDefaultConfig(config),
  requiresContext: ['product_context']
};
