import {
  getShippingCountries,
  getContactInfos,
  getCurrentPageId,
  getOrganizations,
  getCurrentOrganizationId
} from '@oracle-cx-commerce/commerce-utils/selector';

export const getComponentData = state => {
  const shippingCountries = getShippingCountries(state);
  const contactInfos = getContactInfos(state);
  const currentPageId = getCurrentPageId(state);
  const currentOrganization = getOrganizations(state)[getCurrentOrganizationId(state)];
  const defaultShippingAddressId = currentOrganization ? currentOrganization.shippingAddress : '';
  const defaultBillingAddressId = currentOrganization ? currentOrganization.billingAddress : '';

  return {
    shippingCountries,
    contactInfos,
    currentPageId,
    defaultShippingAddressId,
    defaultBillingAddressId
  };
};
