import React from 'react';
import css from '@oracle-cx-commerce/react-widgets/profile/account-name/styles.css';
import {getComponentData} from '@oracle-cx-commerce/react-widgets/profile/account-name/selectors';
import {connect} from '@oracle-cx-commerce/react-components/provider';
import Styled from '@oracle-cx-commerce/react-components/styled';
import PropTypes from 'prop-types';
/**
 * A component for showing organizations that a profile is part of.
 * @param props
 */
const AccountName = props => {
  const {isUserLoggedIn, currentOrganizationId, organizationName} = props;

  return (
    <Styled id="AccountName" css={css}>
      {isUserLoggedIn && currentOrganizationId && (
        <div className="AccountName">
          <span>{organizationName}</span>
        </div>
      )}
    </Styled>
  );
};

AccountName.propTypes = {
  /**
   * This value is used to check if the user is logged in
   */
  isUserLoggedIn: PropTypes.string,
  /**
   * This value is used to indicate the current organization id of a B2B user
   */
  currentOrganizationId: PropTypes.string,
  /**
   * This value is used to indicate the organization name of a B2B user
   */
  organizationName: PropTypes.string.isRequired
};

AccountName.defaultProps = {
  isUserLoggedIn: false,
  currentOrganizationId: undefined
};
export default connect(getComponentData)(AccountName);
