import * as resourceBundle from '@oracle-cx-commerce/resources';

import {buildConfigResources} from '@oracle-cx-commerce/resources/utils';
import {mergeDefaultConfig} from '@oracle-cx-commerce/react-widgets/config';

export const accountPickerConfigResourceKeys = [
  'configNumberOfOrganizationsToShowLabel',
  'configNumberOfOrganizationToShowHelpText'
];

export const accountPickerConfigProperties = [
  {
    id: 'numberOfOrganizationToShow',
    type: 'stringType',
    labelResourceId: 'configNumberOfOrganizationsToShowLabel',
    helpTextResourceId: 'configNumberOfOrganizationToShowHelpText',
    defaultValue: '5',
    pattern: '^[0-9]*$'
  }
];

const accountPickerConfig = mergeDefaultConfig({
  properties: accountPickerConfigProperties,
  locales: buildConfigResources(resourceBundle, accountPickerConfigResourceKeys)
});

export default accountPickerConfig;
