import React from 'react';
import Modal from '@oracle-cx-commerce/react-components/modal';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/profile/add-items-to-cart/components/merge-cart-items/styles.css';
import Alert from '@oracle-cx-commerce/react-components/alert';
/**
 * Component to display alert modal
 * for merging the items to cart
 */
const MergeCartItemsModal = props => {
  const {
    alertMergeItemsToCart,
    alertMergeItemsToCartConfirm,
    textShoppingCartNotEmpty,
    actionConfirm,
    actionCancel,
    closeLinkAltText,
    showMergeCartItemsModal = () => {},
    closeMergeCartItemsModal = () => {},
    addSelectedItemsToCartHandler = () => {},
    cssOverride
  } = props;

  return (
    <Styled id="MergeCartItems" css={css}>
      <Modal
        className="MergeCartItemsModal"
        cssOverride={cssOverride}
        show={showMergeCartItemsModal}
        onClose={closeMergeCartItemsModal}
        closeIconTitle={closeLinkAltText}
        closeArialLabel={closeLinkAltText}
        title={textShoppingCartNotEmpty}
      >
        <section className="MergeCartItems__Main">
          <div>
            <div className="MergeCartItems__AlertBox">
              <Alert id="MergeCartItemsDeleteWarning" type="warning">
                <div className="MergeCartItems__WarnMessageContainer">
                  <div className="MergeCartItems_AlertMessage">{alertMergeItemsToCart}</div>
                  <div className="MergeCartItems_ConfirmMessage">{alertMergeItemsToCartConfirm}</div>
                </div>
              </Alert>
            </div>

            <div className="MergeCartItems__Controls">
              <div className="MergeCartItems__ConfirmButton">
                <button
                  className="MergeCartItems__ConfirmButton"
                  type="button"
                  onClick={addSelectedItemsToCartHandler}
                  aria-label={actionConfirm}
                >
                  {actionConfirm}
                </button>
              </div>
              <div className="MergeCartItems__CancelButton">
                <button
                  className={'MergeCartItems__CancelButton secondary'}
                  type="button"
                  onClick={closeMergeCartItemsModal}
                  aria-label={actionCancel}
                >
                  {actionCancel}
                </button>
              </div>
            </div>
          </div>
        </section>
      </Modal>
    </Styled>
  );
};

export default React.memo(MergeCartItemsModal);
