import PropTypes from 'prop-types';
import React from 'react';
import ToggleSwitch from '@oracle-cx-commerce/react-components/toggle-switch';

/**
 * Component to show Active status of the user.
 * @param {Object} props the properties object
 */
const ActiveSwitch = props => {
  const {isActive, activeChangeHandler, textActive, disabled} = props;

  return (
    <div className="ActiveSwitch__Section">
      <ToggleSwitch
        id="edit-account-contact-active-switch"
        isOn={isActive}
        className="ActiveSwitch__ToggleSwitch"
        onChange={activeChangeHandler}
        label={textActive}
        disabled={disabled ? disabled : ''}
      />
      <label htmlFor="edit-account-contact-active-switch" className="ActiveSwitch__ActiveText">
        {textActive}
      </label>
    </div>
  );
};

ActiveSwitch.propTypes = {
  /**
   * Flag to indicate Active status of the user whose profile is added/edited.
   */
  isActive: PropTypes.bool.isRequired,
  /**
   * Function to handle onChange event of the switch.
   */
  activeChangeHandler: PropTypes.func.isRequired,
  /**
   * Flag to indicate if active switch should be disabled for change.
   */
  disabled: PropTypes.bool
};
ActiveSwitch.defaultProps = {
  disabled: false
};
export default ActiveSwitch;
