import Link from '@oracle-cx-commerce/react-components/link';
import {PAGE_ACCOUNT_CONTACTS_LINK} from '@oracle-cx-commerce/commerce-utils/constants';
import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/profile/add-or-edit-account-contact/components/cancel-save-actions/styles.css';
/**
 * Component to show Save button and Cancel Link.
 * @param {Object} props the properties object
 */
const CancelSaveActions = props => {
  const {labelCancel, actionSave} = props;

  return (
    <Styled id="CancelSaveActions" css={css}>
      <div className="CancelSaveActions">
        <button type="submit" className="CancelSaveActions__SaveButton">
          {actionSave}
        </button>
        <Link className="CancelSaveActions__CancelLink" href={PAGE_ACCOUNT_CONTACTS_LINK}>
          {labelCancel}
        </Link>
      </div>
    </Styled>
  );
};

export default CancelSaveActions;
