import PropTypes from 'prop-types';
import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/profile/add-or-edit-account-contact/components/user-name-section/styles.css';
/**
 * Component to show First Name & Last Name of the user.
 * @param {Object} props the properties object
 */
const UserNameSection = props => {
  const {id, labelFirstName, labelLastName, firstName, lastName} = props;

  return (
    <Styled id="NameSection" css={css}>
      <div className="NameSection__Container">
        <div className="NameSection__Name">
          <label htmlFor={`firstname-${id}`}>{labelFirstName}</label>
          <input
            id={`firstname-${id}`}
            aria-label={labelFirstName}
            type="text"
            name="firstName"
            defaultValue={firstName}
            required
            autoCapitalize="words"
            autoComplete="given-name"
            maxLength="254"
          ></input>
          <span className="validationMessage"></span>
        </div>
        <div className="NameSection__Name">
          <label htmlFor={`lastname-${id}`}>{labelLastName}</label>
          <input
            id={`lastname-${id}`}
            aria-label={labelLastName}
            type="text"
            name="lastName"
            defaultValue={lastName}
            required
            autoCapitalize="words"
            autoComplete="family-name"
            maxLength="254"
          />
          <span className="validationMessage"></span>
        </div>
      </div>
    </Styled>
  );
};

UserNameSection.propTypes = {
  /**
   * First Name of the contact
   */
  firstName: PropTypes.string,
  /**
   * Last Name of the contact
   */
  lastName: PropTypes.string,
  /**
   * Unique identifier for id attribute of html elements
   */
  id: PropTypes.string
};
UserNameSection.defaultProps = {
  id: '',
  firstName: undefined,
  lastName: undefined
};
export default UserNameSection;
