import {getCancelReasons, getOrder, getPage} from '@oracle-cx-commerce/commerce-utils/selector';

export const getOrderDetails = state => {
  const orderId = getPage(state).contextId;
  const orderDetails = getOrder(state, {id: orderId});
  const {state: orderState, commerceItems = {}, shippingGroups = {}} = orderDetails;
  const cancelReasons = getCancelReasons(state);

  return {
    orderId,
    cancelReasons,
    commerceItems,
    shippingGroups,
    orderState
  };
};
