import {
  isAuthenticated,
  getCurrentProfileId,
  getContactRequest,
  getPage
} from '@oracle-cx-commerce/commerce-utils/selector';

export const getComponentData = state => {
  const registrationId = getPage(state).contextId;
  const contactRequest = getContactRequest(state, {id: registrationId});
  const isUserLoggedIn = isAuthenticated(state) && getCurrentProfileId(state) !== 'anonymous';

  return {
    registrationId,
    contactRequest,
    isUserLoggedIn
  };
};
