import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/profile/contact-registration-requests/components/registration-request-summary-information/styles.css';
import PropTypes from 'prop-types';

/**
 * Displays a label describing the results for search
 * @param props
 * @param {*} searchFilter The search value entered in the search box
 * @param {*} textRegistrationRequestSummary The text that will be displayed when the search is successful.
 */
const RegistrationRequestsSummaryInformation = props => {
  const {searchFilter, textRegistrationRequestSummary} = props;
  if (searchFilter) {
    return (
      <Styled id="RegistrationRequestsSummaryInformation" css={css}>
        <div className="RegistrationRequestsSummaryInformation__Container">
          <span>{textRegistrationRequestSummary}</span>
          <span className="RegistrationRequestsSummaryInformation__SearchText">{`"${searchFilter}"`}</span>
        </div>
      </Styled>
    );
  }

  return <div />;
};

RegistrationRequestsSummaryInformation.propTypes = {
  /**
   * The value for search input
   */
  searchFilter: PropTypes.string
};

RegistrationRequestsSummaryInformation.defaultProps = {
  searchFilter: ''
};

export default RegistrationRequestsSummaryInformation;
