import {isAuthenticated, getCurrentProfileId, getContactRequests} from '@oracle-cx-commerce/commerce-utils/selector';

export const getComponentData = state => {
  const profileId = getCurrentProfileId(state);
  const isUserLoggedIn = isAuthenticated(state) && getCurrentProfileId(state) !== 'anonymous';
  const listContactRequests = getContactRequests(state);

  return {
    profileId,
    isUserLoggedIn,
    listContactRequests
  };
};
