export const defaultSortProperty = 'createdTime:desc';

export const CONTACT_REQUEST_STATES = {
  new: 'textRegistrationRequestsNew',
  review: 'textRegistrationRequestsReview',
  rejected: 'textRegistrationRequestRejected',
  moreInfoNeeded: 'textMoreInfoRequired'
};

export const buildQuerySQLPram = (lastNameFilter, filterValue) => {
  let query = 'status ne "APPROVED"';
  if (lastNameFilter) {
    query += ` AND profile.lastName co "${lastNameFilter}"`;
  }
  if (filterValue) {
    query += ` AND status eq "${filterValue}"`;
  }

  return query;
};
