import {
  getCurrentProfileId,
  getInquireBalanceResponseForCurrentUser
} from '@oracle-cx-commerce/commerce-utils/selector';

export const getLoyaltyData = state => {
  const profileId = getCurrentProfileId(state);
  const {loyaltyPrograms = {}} = getInquireBalanceResponseForCurrentUser(state);

  return {
    profileId,
    loyaltyPrograms
  };
};
