import * as resourceBundle from '@oracle-cx-commerce/resources';
import {buildResources} from '@oracle-cx-commerce/resources/utils';
import defaultConfig from '@oracle-cx-commerce/react-widgets/config';

const widgetResourceKeys = [
  'labelApproverComments',
  'labelApproveOrder',
  'labelRejectOrder',
  'textOrderApprovedMessage',
  'textOrderRejectedMessage',
  'textScheduledOrderApprovedMessage',
  'textScheduledOrderRejectedMessage'
];

export const OrderApproval = {
  packageId: '@oracle-cx-commerce/react-widgets',
  resources: buildResources(resourceBundle, widgetResourceKeys),
  availableToAllPages: false,
  pageTypes: ['scheduledOrder', 'profile'],
  config: defaultConfig,
  actions: ['approveOrder', 'rejectOrder', 'notify'],
  requiresContext: ['order_context']
};
