import {isAuthenticated, getCurrentSiteId} from '@oracle-cx-commerce/commerce-utils/selector';

export const getPendingApprovalOrdersData = state => {
  const siteId = getCurrentSiteId(state);
  const isUserLoggedIn = isAuthenticated(state);

  return {
    siteId,
    isUserLoggedIn
  };
};
