import {
  getContactInfos,
  getCurrentOrganizationId,
  getOrganizations,
  getRoles
} from '@oracle-cx-commerce/commerce-utils/selector';

export const getComponentData = state => {
  const contactInfos = getContactInfos(state);
  const currentOrganization = getOrganizations(state)[getCurrentOrganizationId(state)] || {};

  const {shippingAddress = '', billingAddress = ''} = currentOrganization;
  const roles = getRoles(state);
  const defaultShippingAddress = shippingAddress ? contactInfos[shippingAddress] || {} : {};
  const defaultBillingAddress = billingAddress ? contactInfos[billingAddress] || {} : {};

  return {defaultShippingAddress, defaultBillingAddress, roles};
};
