import Card from '@oracle-cx-commerce/react-components/card';
import ContactIcon from '@oracle-cx-commerce/react-components/icons/contact';
import Link from '@oracle-cx-commerce/react-components/link';
import {PAGE_ACCOUNT_CONTACTS_LINK} from '@oracle-cx-commerce/commerce-utils/constants';
import PropTypes from 'prop-types';
import React from 'react';
import Styled from '@oracle-cx-commerce/react-components/styled';
import css from '@oracle-cx-commerce/react-widgets/profile/profile-account-contacts-summary/components/account-contacts-details/styles.css';

/**
 * Widget to display the profile contacts summary details at profile page.
 * @param {Object} props the properties object
 */
const AccountContactsDetails = props => {
  const {actionManage, textActiveContacts, textInActiveContacts} = props;
  const {inActiveContacts, activeContacts} = props;

  return (
    <Styled id="AccountContactsDetails" css={css}>
      <div className="AccountContactsDetails__Card">
        <Card>
          <div className="AccountContactsDetails__Contacts">
            <div className="AccountContactsDetails__RowItem">
              <div className="AccountContactsDetails__ActiveIcon">
                <ContactIcon />
              </div>
              <div>
                <span className="AccountContactsDetails__TotalContacts">{activeContacts}</span> {textActiveContacts}
              </div>
            </div>
            <div className="AccountContactsDetails__RowItem">
              <div className="AccountContactsDetails__InactiveIcon">
                <ContactIcon />
              </div>
              <div>
                <span className="AccountContactsDetails__TotalContacts">{inActiveContacts}</span> {textInActiveContacts}
              </div>
            </div>
          </div>
          <div className="AccountContactsDetails__Manage">
            <Link href={PAGE_ACCOUNT_CONTACTS_LINK}>{actionManage}</Link>
          </div>
        </Card>
      </div>
    </Styled>
  );
};

AccountContactsDetails.propTypes = {
  /**
   * The number of in active contacts
   */
  inActiveContacts: PropTypes.number,

  /**
   * The number of in active contacts
   */
  activeContacts: PropTypes.number
};

AccountContactsDetails.defaultProps = {
  inActiveContacts: 0,
  activeContacts: 0
};

export default AccountContactsDetails;
